import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/TestPage.css";

const TestPage = () => {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState({});
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:5000/api/questions/random/10")
      .then((res) => setQuestions(res.data))
      .catch((err) => console.error(err));
  }, []);

  const handleAnswer = (questionId, answer) => {
    setUserAnswers({ ...userAnswers, [questionId]: answer });
  };

  const handleSubmit = async () => {
    try {
      const formattedAnswers = questions.map((q) => ({
        question: q.text,
        userAnswer: userAnswers[q._id] || "",
        correctAnswer: q.correctAnswer,
      }));

      const response = await axios.post("http://localhost:5000/api/tests", {
        name,
        email,
        answers: formattedAnswers,
      });

      setScore(response.data.scorePercent);
      setSubmitted(true);
    } catch (err) {
      console.error("Error submitting test:", err);
      alert("Failed to submit test.");
    }
  };

  if (submitted) {
    return (
      <div className="test-page">
        <div className="test-card">
          <h2>Test Submitted Successfully ðŸŽ‰</h2>
          <p><strong>Name:</strong> {name}</p>
          <p><strong>Email:</strong> {email}</p>
          <h3>Your Score: {score}%</h3>
        </div>
      </div>
    );
  }

  return (
    <div className="test-page">
      <div className="test-card">
        <h2>VLSI Interview Test</h2>

        <label>Full Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        <label>Email Address:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        {questions.length > 0 ? (
          <>
            <h3>
              Q{currentIndex + 1}. {questions[currentIndex].text}
            </h3>
            {questions[currentIndex].options.map((opt, i) => (
              <div key={i}>
                <label>
                  <input
                    type="radio"
                    name={`q-${currentIndex}`}
                    value={opt}
                    checked={userAnswers[questions[currentIndex]._id] === opt}
                    onChange={() =>
                      handleAnswer(questions[currentIndex]._id, opt)
                    }
                  />
                  {opt}
                </label>
              </div>
            ))}

            <div className="test-controls">
              <button
                disabled={currentIndex === 0}
                onClick={() => setCurrentIndex(currentIndex - 1)}
              >
                Previous
              </button>

              {currentIndex < questions.length - 1 ? (
                <button onClick={() => setCurrentIndex(currentIndex + 1)}>
                  Next
                </button>
              ) : (
                <button className="btn-submit" onClick={handleSubmit}>
                  Submit Test
                </button>
              )}
            </div>
          </>
        ) : (
          <p>Loading questions...</p>
        )}
      </div>
    </div>
  );
};

export default TestPage;


