import React, { useEffect, useState } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";
import "../styles/AdminDashboard.css";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [results, setResults] = useState([]);
  const [activeTab, setActiveTab] = useState("questions"); // ðŸ”¹ Switch tab
  const [form, setForm] = useState({
    questionText: "",
    optionsText: "",
    correctAnswer: "",
  });
  const [questionType, setQuestionType] = useState("MCQ");
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingResults, setLoadingResults] = useState(false);

  // ðŸŸ¢ Check admin login
  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) navigate("/admin/login");
  }, [navigate]);

  // ðŸŸ¢ Fetch initial data
  useEffect(() => {
    fetchQuestions();
    fetchResults();
  }, []);

  const fetchQuestions = async () => {
    try {
      const res = await API.get("/questions");
      setQuestions(res.data || []);
    } catch (err) {
      console.error(err);
      alert("âŒ Failed to load questions.");
    }
  };

  const fetchResults = async () => {
    setLoadingResults(true);
    try {
      const res = await API.get("/tests");
      setResults(res.data || []);
    } catch (err) {
      console.error(err);
      alert("âŒ Failed to load test results.");
    } finally {
      setLoadingResults(false);
    }
  };

  // ðŸŸ¢ Add Question
  const handleAddQuestion = async (e) => {
    e.preventDefault();
    const opts =
      questionType === "MCQ"
        ? form.optionsText.split("|").map((s) => s.trim()).filter(Boolean)
        : [];

    if (!form.questionText.trim()) {
      return alert("Please enter question text.");
    }
    if (questionType === "MCQ" && (opts.length < 2 || !form.correctAnswer.trim())) {
      return alert("Please enter at least 2 options and a correct answer.");
    }

    setLoading(true);
    try {
      await API.post("/questions", {
        questionType,
        questionText: form.questionText,
        options: opts,
        correctAnswer: questionType === "MCQ" ? form.correctAnswer : "",
      });

      setForm({ questionText: "", optionsText: "", correctAnswer: "" });
      await fetchQuestions();
      alert("âœ… Question added successfully!");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Failed to add question.");
    } finally {
      setLoading(false);
    }
  };

  // ðŸŸ¢ Upload File
  const handleUpload = async () => {
    if (!file) return alert("Please select a file to upload.");
    const fd = new FormData();
    fd.append("file", file);

    try {
      const res = await API.post("/questions/upload", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(res.data.message || "âœ… File uploaded successfully!");
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "âŒ File upload failed.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    navigate("/admin/login");
  };

  return (
    <div className="admin-dashboard">
      {/* Header */}
      <div className="admin-header">
        <h2>Admin Dashboard</h2>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Tabs */}
      <div className="tab-buttons">
        <button
          className={`tab-btn ${activeTab === "questions" ? "active" : ""}`}
          onClick={() => setActiveTab("questions")}
        >
          ðŸ§© Questions
        </button>
        <button
          className={`tab-btn ${activeTab === "results" ? "active" : ""}`}
          onClick={() => setActiveTab("results")}
        >
          ðŸ“Š Test Results
        </button>
      </div>

      {/* Content based on active tab */}
      {activeTab === "questions" ? (
        <div className="tab-content">
          {/* Add Question / Upload Section */}
          <div className="card form-section">
            <h5>Add or Upload Questions</h5>

            <form onSubmit={handleAddQuestion}>
              <div className="mb-3">
                <label className="form-label">Question Type</label>
                <select
                  className="form-select"
                  value={questionType}
                  onChange={(e) => setQuestionType(e.target.value)}
                >
                  <option value="MCQ">MCQ</option>
                  <option value="Theory">Theory</option>
                </select>
              </div>

              <div className="mb-3">
                <label className="form-label">Question Text</label>
                <textarea
                  className="form-control"
                  rows={questionType === "Theory" ? 3 : 2}
                  value={form.questionText}
                  onChange={(e) =>
                    setForm({ ...form, questionText: e.target.value })
                  }
                  placeholder="Enter your question here..."
                />
              </div>

              {questionType === "MCQ" && (
                <>
                  <div className="mb-3">
                    <label className="form-label">Options (separate by |)</label>
                    <input
                      className="form-control"
                      value={form.optionsText}
                      onChange={(e) =>
                        setForm({ ...form, optionsText: e.target.value })
                      }
                      placeholder="Option A | Option B | Option C | Option D"
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Correct Answer</label>
                    <input
                      className="form-control"
                      value={form.correctAnswer}
                      onChange={(e) =>
                        setForm({ ...form, correctAnswer: e.target.value })
                      }
                      placeholder="Enter correct answer text"
                    />
                  </div>
                </>
              )}

              <div className="d-flex gap-2">
                <button className="btn btn-success" type="submit" disabled={loading}>
                  {loading ? "Adding..." : "Add Question"}
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() =>
                    setForm({ questionText: "", optionsText: "", correctAnswer: "" })
                  }
                >
                  Clear
                </button>
              </div>
            </form>

            <hr className="my-4" />

            {/* Upload Document Section */}
            <div className="upload-section">
              <label className="form-label">Upload Document (.docx)</label>
              <input
                type="file"
                className="form-control"
                accept=".docx"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <div className="mt-2">
                <button className="btn btn-primary" onClick={handleUpload}>
                  Upload
                </button>
              </div>
            </div>
          </div>

          {/* Display Available Questions */}
          <div className="card available-questions">
            <h5>Available Questions</h5>

            <h6 className="mt-3 text-primary">MCQ Questions</h6>
            {questions.filter((q) => q.questionType === "MCQ").length === 0 && (
              <div className="text-muted">No MCQs yet</div>
            )}
            {questions
              .filter((q) => q.questionType === "MCQ")
              .map((q, idx) => (
                <div key={q._id} className="question-item">
                  <strong>Q{idx + 1}:</strong> {q.questionText}
                  <div className="small text-muted">
                    Options: {q.options.join(" | ")}
                  </div>
                  <div className="small text-success">
                    Correct: {q.correctAnswer}
                  </div>
                </div>
              ))}

            <h6 className="mt-4 text-primary">Theory Questions</h6>
            {questions.filter((q) => q.questionType === "Theory").length === 0 && (
              <div className="text-muted">No theory questions yet</div>
            )}
            {questions
              .filter((q) => q.questionType === "Theory")
              .map((q, idx) => (
                <div key={q._id} className="question-item">
                  <strong>Q{idx + 1}:</strong> {q.questionText}
                </div>
              ))}
          </div>
        </div>
      ) : (
        <div className="tab-content">
          {/* Test Results Section */}
          <div className="card results-section">
            <h5>Candidate Test Results</h5>

            {loadingResults ? (
              <div>Loading test results...</div>
            ) : results.length === 0 ? (
              <div className="text-muted">No test results yet</div>
            ) : (
              <table className="results-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Total Questions</th>
                    <th>Correct</th>
                    <th>Score %</th>
                    <th>Submitted At</th>
                  </tr>
                </thead>
                <tbody>
                  {results.map((res, idx) => (
                    <tr key={idx}>
                      <td>{res.name}</td>
                      <td>{res.email}</td>
                      <td>{res.totalQuestions}</td>
                      <td>{res.correctAnswers}</td>
                      <td>{res.scorePercent}%</td>
                      <td>{new Date(res.submittedAt).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

