import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/Home.css";

export default function Home() {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [userId, setUserId] = useState(null);
  const [waiting, setWaiting] = useState(false);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // üü¢ Restore previous session (in case of refresh)
  useEffect(() => {
    const storedUser = localStorage.getItem("testUser");
    if (storedUser) {
      const { fullName, email, userId, status } = JSON.parse(storedUser);
      setFullName(fullName);
      setEmail(email);
      setUserId(userId);
      if (status === "waiting") setWaiting(true);
      if (status === "active") {
        // If already active ‚Üí directly open test page
        navigate("/test", { state: { fullName, email } });
      }
    }
  }, [navigate]);

  // üü¢ When user clicks ‚ÄúStart Test‚Äù
  const handleStart = async () => {
    if (!fullName.trim() || !email.trim()) {
      return alert("Please enter your full name and email.");
    }

    setLoading(true);
    try {
      // Register user in DB with status = "waiting"
      const res = await API.post("/user/register", { fullName, email });
      const userId = res.data.userId;
      setUserId(userId);
      setWaiting(true);

      // Save locally for refresh
      localStorage.setItem(
        "testUser",
        JSON.stringify({ fullName, email, userId, status: "waiting" })
      );

      alert("‚úÖ Registered successfully! Waiting for admin to start the test.");
    } catch (err) {
      console.error("‚ùå Error registering user:", err);
      alert("Failed to register. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // üïí Poll for admin status (to start the test)
  useEffect(() => {
    if (!userId) return;

    const interval = setInterval(async () => {
      try {
        const res = await API.get(`/user/status/${userId}`);
        const currentStatus = res.data.status;

        if (currentStatus === "active") {
          // Save updated status and go inside test page
          localStorage.setItem(
            "testUser",
            JSON.stringify({ fullName, email, userId, status: "active" })
          );
          clearInterval(interval);
          navigate("/test", { state: { fullName, email } });
        }
      } catch (err) {
        console.error("Error checking test status:", err);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [userId, fullName, email, navigate]);

  // üü¢ Waiting screen (simple & clean)
  if (waiting) {
    return (
      <div className="container container-center">
        <div className="card card-clean p-4 text-center">
          <h3 className="text-warning">‚è≥ Waiting for Admin to Start Test</h3>
          <p>
            Hi <strong>{fullName}</strong> (<em>{email}</em>)
          </p>
          <p>Please keep this page open. Your test will start automatically.</p>
          <div className="spinner-border text-warning mt-3" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  // üü¢ Default Home screen
  return (
    <div className="container container-center">
      <div className="header-banner mb-4">
        <h1 className="display-6">VLSI Interview Assessment</h1>
        <p className="lead">
          Test your knowledge of Very Large Scale Integration (VLSI) concepts
        </p>
      </div>

      <div className="card card-clean p-4 mb-4">
        <h5>Start Your Interview Test</h5>
        <p className="text-muted">
          You will be presented with randomly selected questions. Please answer
          to the best of your ability.
        </p>

        <div className="row g-3">
          <div className="col-md-6">
            <label className="form-label">Full Name *</label>
            <input
              className="form-control"
              placeholder="Enter your full name"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              disabled={loading}
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Email Address *</label>
            <input
              type="email"
              className="form-control"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={loading}
            />
          </div>

          <div className="col-12 mt-3">
            <button
              className="btn btn-primary btn-lg w-100"
              onClick={handleStart}
              disabled={loading}
            >
              {loading ? "Registering..." : "Start Test"}
            </button>
          </div>
        </div>
      </div>

      <div className="card card-clean p-3">
        <h6>Instructions</h6>
        <ul>
          <li>Read each question carefully before answering.</li>
          <li>Use Next and Previous to navigate.</li>
          <li>Submit once all questions are answered.</li>
          <li>Do not close or refresh this page once the test has started.</li>
        </ul>
      </div>
    </div>
  );
}

