import React, { useEffect, useState } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";
import "../styles/AdminDashboard.css";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [results, setResults] = useState([]);
  const [activeTab, setActiveTab] = useState("questions");
  const [form, setForm] = useState({
    questionText: "",
    optionsText: "",
    correctAnswer: "",
  });
  const [questionType, setQuestionType] = useState("MCQ");
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingResults, setLoadingResults] = useState(false);
  const [editingId, setEditingId] = useState(null);

  // ðŸŸ¢ Check admin login
  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) navigate("/admin/login");
  }, [navigate]);

  // ðŸŸ¢ Initial fetch
  useEffect(() => {
    fetchQuestions();
    fetchResults();
  }, []);

  const fetchQuestions = async () => {
    try {
      const res = await API.get("/questions");
      setQuestions(res.data || []);
    } catch (err) {
      console.error(err);
      alert("âŒ Failed to load questions.");
    }
  };

  const fetchResults = async () => {
    setLoadingResults(true);
    try {
      const res = await API.get("/tests");
      setResults(res.data || []);
    } catch (err) {
      console.error(err);
      alert("âŒ Failed to load test results.");
    } finally {
      setLoadingResults(false);
    }
  };

  // ðŸŸ¢ Add or Update Question
  const handleAddOrUpdate = async (e) => {
    e.preventDefault();
    const opts =
      questionType === "MCQ"
        ? form.optionsText.split("|").map((s) => s.trim()).filter(Boolean)
        : [];

    if (!form.questionText.trim()) {
      return alert("Please enter question text.");
    }

    setLoading(true);
    try {
      if (editingId) {
        await API.put(`/questions/${editingId}`, {
          questionType,
          questionText: form.questionText,
          options: opts,
          correctAnswer: questionType === "MCQ" ? form.correctAnswer : "",
        });
        alert("âœ… Question updated successfully!");
      } else {
        await API.post("/questions", {
          questionType,
          questionText: form.questionText,
          options: opts,
          correctAnswer: questionType === "MCQ" ? form.correctAnswer : "",
        });
        alert("âœ… Question added successfully!");
      }

      setForm({ questionText: "", optionsText: "", correctAnswer: "" });
      setEditingId(null);
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "âŒ Operation failed.");
    } finally {
      setLoading(false);
    }
  };

  // ðŸŸ¢ Delete Question
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this question?")) return;

    try {
      await API.delete(`/questions/${id}`);
      alert("ðŸ—‘ï¸ Question deleted successfully!");
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert("âŒ Failed to delete question.");
    }
  };

  // ðŸŸ¢ Edit Question
  const handleEdit = (q) => {
    setEditingId(q._id);
    setQuestionType(q.questionType);
    setForm({
      questionText: q.questionText,
      optionsText: q.options ? q.options.join(" | ") : "",
      correctAnswer: q.correctAnswer || "",
    });
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // ðŸŸ¢ Upload File
  const handleUpload = async () => {
    if (!file) return alert("Please select a file to upload.");
    const fd = new FormData();
    fd.append("file", file);

    try {
      const res = await API.post("/questions/upload", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(res.data.message || "âœ… File uploaded successfully!");
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "âŒ File upload failed.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    navigate("/admin/login");
  };

  return (
    <div className="admin-dashboard">
      {/* Header */}
      <div className="admin-header">
        <h2>Admin Dashboard</h2>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Tabs */}
      <div className="tab-buttons">
        <button
          className={`tab-btn ${activeTab === "questions" ? "active" : ""}`}
          onClick={() => setActiveTab("questions")}
        >
          ðŸ§© Questions
        </button>
        <button
          className={`tab-btn ${activeTab === "results" ? "active" : ""}`}
          onClick={() => setActiveTab("results")}
        >
          ðŸ“Š Test Results
        </button>
      </div>

      {/* ðŸ§© QUESTIONS TAB */}
      {activeTab === "questions" ? (
        <div className="tab-content">
          <div className="card form-section">
            <h5>{editingId ? "âœï¸ Edit Question" : "âž• Add or Upload Questions"}</h5>

            <form onSubmit={handleAddOrUpdate}>
              <div className="mb-3">
                <label className="form-label">Question Type</label>
                <select
                  className="form-select"
                  value={questionType}
                  onChange={(e) => setQuestionType(e.target.value)}
                >
                  <option value="MCQ">MCQ</option>
                  <option value="Theory">Theory</option>
                </select>
              </div>

              <div className="mb-3">
                <label className="form-label">Question Text</label>
                <textarea
                  className="form-control"
                  rows={questionType === "Theory" ? 3 : 2}
                  value={form.questionText}
                  onChange={(e) =>
                    setForm({ ...form, questionText: e.target.value })
                  }
                  placeholder="Enter your question here..."
                />
              </div>

              {questionType === "MCQ" && (
                <>
                  <div className="mb-3">
                    <label className="form-label">Options (separate by |)</label>
                    <input
                      className="form-control"
                      value={form.optionsText}
                      onChange={(e) =>
                        setForm({ ...form, optionsText: e.target.value })
                      }
                      placeholder="Option A | Option B | Option C | Option D"
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Correct Answer</label>
                    <input
                      className="form-control"
                      value={form.correctAnswer}
                      onChange={(e) =>
                        setForm({ ...form, correctAnswer: e.target.value })
                      }
                      placeholder="Enter correct answer text"
                    />
                  </div>
                </>
              )}

              <div className="d-flex gap-2">
                <button className="btn btn-success" type="submit" disabled={loading}>
                  {loading
                    ? "Saving..."
                    : editingId
                    ? "Update Question"
                    : "Add Question"}
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setForm({
                      questionText: "",
                      optionsText: "",
                      correctAnswer: "",
                    });
                    setEditingId(null);
                  }}
                >
                  Clear
                </button>
              </div>
            </form>

            <hr className="my-4" />

            {/* Upload Section */}
            <div className="upload-section">
              <label className="form-label">Upload Document (.docx)</label>
              <input
                type="file"
                className="form-control"
                accept=".docx"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <div className="mt-2">
                <button className="btn btn-primary" onClick={handleUpload}>
                  Upload
                </button>
              </div>
            </div>
          </div>

          {/* Display Questions */}
          <div className="card available-questions">
            <h5>Available Questions</h5>

            {/* MCQ Section */}
            <h6 className="mt-3 text-primary">MCQ Questions</h6>
            {questions.filter((q) => q.questionType === "MCQ").length === 0 && (
              <div className="text-muted">No MCQs yet</div>
            )}
            {questions
              .filter((q) => q.questionType === "MCQ")
              .map((q, idx) => (
                <div key={q._id} className="question-item">
                  <strong>Q{idx + 1}:</strong> {q.questionText}
                  <div className="small text-muted">
                    Options: {q.options.join(" | ")}
                  </div>
                  <div className="small text-success">
                    Correct: {q.correctAnswer}
                  </div>
                  <div className="action-buttons">
                    <button
                      className="btn btn-sm btn-warning"
                      onClick={() => handleEdit(q)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(q._id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}

            {/* Theory Section */}
            <h6 className="mt-4 text-primary">Theory Questions</h6>
            {questions.filter((q) => q.questionType === "Theory").length === 0 && (
              <div className="text-muted">No theory questions yet</div>
            )}
            {questions
              .filter((q) => q.questionType === "Theory")
              .map((q, idx) => {
                const relatedAnswers = results
                  .flatMap((r) => r.answers || [])
                  .filter(
                    (a) =>
                      a.question.trim().toLowerCase() ===
                      q.questionText.trim().toLowerCase()
                  );
                return (
                  <div key={q._id} className="question-item">
                    <strong>Q{idx + 1}:</strong> {q.questionText}
                    {relatedAnswers.length > 0 ? (
                      <div className="theory-answers">
                        <b>Answers:</b>
                        <ul>
                          {relatedAnswers.map((a, i) => (
                            <li key={i}>{a.userAnswer}</li>
                          ))}
                        </ul>
                      </div>
                    ) : (
                      <div className="text-muted small">No answers yet</div>
                    )}
                    <div className="action-buttons">
                      <button
                        className="btn btn-sm btn-warning"
                        onClick={() => handleEdit(q)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => handleDelete(q._id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      ) : (
        // ðŸ“Š TEST RESULTS TAB
        <div className="tab-content">
          <div className="card results-section">
            <h5>Candidate Test Results</h5>
            {loadingResults ? (
              <div>Loading test results...</div>
            ) : results.length === 0 ? (
              <div className="text-muted">No test results yet</div>
            ) : (
              <table className="results-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Total</th>
                    <th>Correct</th>
                    <th>Score %</th>
                    <th>Submitted At</th>
                  </tr>
                </thead>
                <tbody>
                  {results.map((res, idx) => (
                    <tr key={idx}>
                      <td>{res.name}</td>
                      <td>{res.email}</td>
                      <td>{res.totalQuestions}</td>
                      <td>{res.correctAnswers}</td>
                      <td>{res.scorePercent}%</td>
                      <td>{new Date(res.submittedAt).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

